<html>	<head></head>	<body>		<?php		$field_name = $_POST ['contact-name'];		$field_lastname = $_POST ['contact-lastname'];		$field_email = $_POST ['contact-email'];		$field_message = $_POST ['contact-message'];		$site = $_POST ['site-url'];		$url = $_POST ['redirect'];		
		$mail_to = $_POST ['email-to'];		$subject = 'Message from ' . $site . ' visitor ' . $field_name . ' ' . $field_lastname;		$body_message = 'From: ' . $field_name . ' ' . $field_lastname . "\n";		$body_message .= 'E-mail: ' . $field_email . "\n";		$body_message .= "\n" . $field_message;		$headers = 'From: '.$field_email."\r\n";		$headers .= 'Reply-To: '.$field_email."\r\n";		$mail_status = mail ( $mail_to, $subject, $body_message, $headers );		if ($mail_status) {		?>		<script language="javascript" type="text/javascript">			alert('Thank you for the message. We will contact you shortly.');			window.location = '<?php echo $url?>';		</script>	
		<?php		} else {		?>		<script language="javascript" type="text/javascript">			alert('Message failed. Please, send an email to a.demetriou@qobo.biz');			window.location = '<?php echo $url?>';		</script>
		<?php
		}		?>	</body></html>